$(document).ready(function() {
    $(".navbar-toggle").on("click", function(e) {
        e.preventDefault(), $(".menu").toggleClass("active"), $(this).find("i").toggleClass("active");
    }), $(".menu-item-has-children a").on("click", function(e) {
        e.preventDefault(), $(this).parent().find(".submenu").toggleClass("active"), $(this).parent().find("i").toggleClass("active");
    }), $(".slide-container").slick({
        autoplay: !0,
        autoplaySpeed: 3e3,
        speed: 800,
        adaptiveHeight: !0,
        arrows: !0,
        appendArrows: $(".slide-arrows"),
        prevArrow: '<button><i class="material-icons">keyboard_arrow_left</i></button>',
        nextArrow: '<button><i class="material-icons">keyboard_arrow_right</i></button>',
        dots: !0,
        appendDots: $(".slide-dots")
    });
});